            writer.WriteString("secureSmtpPassword", imapSmtpAccessDetails.SecureSmtpPassword);

            writer.WriteString("smtpServerHost", imapSmtpAccessDetails.SmtpServerHost);

            writer.WriteNumber("smtpServerPort", imapSmtpAccessDetails.SmtpServerPort);

            writer.WriteString("smtpUsername", imapSmtpAccessDetails.SmtpUsername);

            writer.WriteString("smtpPassword", imapSmtpAccessDetails.SmtpPassword);

            writer.WriteString("secureImapServerHost", imapSmtpAccessDetails.SecureImapServerHost);

            writer.WriteNumber("secureImapServerPort", imapSmtpAccessDetails.SecureImapServerPort);

            writer.WriteString("secureImapUsername", imapSmtpAccessDetails.SecureImapUsername);

            writer.WriteString("secureImapPassword", imapSmtpAccessDetails.SecureImapPassword);

            writer.WriteString("imapServerHost", imapSmtpAccessDetails.ImapServerHost);

            writer.WriteNumber("imapServerPort", imapSmtpAccessDetails.ImapServerPort);

            writer.WriteString("imapUsername", imapSmtpAccessDetails.ImapUsername);

            writer.WriteString("imapPassword", imapSmtpAccessDetails.ImapPassword);

            writer.WriteString("imapMailbox", imapSmtpAccessDetails.ImapMailbox);

            if (imapSmtpAccessDetails.MailFromDomainOption.IsSet)
                if (imapSmtpAccessDetails.MailFromDomainOption.Value != null)
                    writer.WriteString("mailFromDomain", imapSmtpAccessDetails.MailFromDomain);
                else
                    writer.WriteNull("mailFromDomain");
        }
    }
}
